implementation.constructor:


this.init = function() {
  if (this.hasAttribute("sivAccepts"))
    this.sivFlavour = this.getAttribute("sivAccepts").split(",");
  else 
    this.sivFlavour = ["sieve/action"];

  if (this.hasAttribute("sivSiblingId"))
    this.sivSiblingId = this.getAttribute("sivSiblingId");
  else
    this.sivSiblingId = null;

  if (this.hasAttribute("sivParentId"))
    this.sivParentId = this.getAttribute("sivParentId");
  else
    this.sivParentId = null;

}

this.canDrop = function(sivFlavour, event) {
  // accept only the registered drop flavour...
  if ( ! event.dataTransfer.mozGetDataAt(sivFlavour,0))
    return false;     

  if (event.dataTransfer.mozGetDataAt(sivFlavour,3) == "create")
    return true;

  if (event.dataTransfer.mozGetDataAt(sivFlavour,3) != "move")
    return false; 

  // it should not be possible to drag the element on to itself...
  if (this.isSameNode(event.dataTransfer.mozGetDataAt(sivFlavour,1)))
    return false;

  if (this.previousSibling)
    if (this.previousSibling.previousSibling)
      if (this.previousSibling.previousSibling.isSameNode(event.dataTransfer.mozGetDataAt(sivFlavour,1)))
        return false;


  // ... or onto a parent into his child block          
  for (var node = this; node; node = node.parentNode)
    if (node.isSameNode(event.dataTransfer.mozGetDataAt(sivFlavour,0)))
      return false;

  return true; 
}

this.moveElement = function(sivFlavour,event) {
  var elm = dom.removeElement(
              event.dataTransfer.mozGetDataAt(sivFlavour,2));

  if (!elm)
    throw "No Element found for "+event.dataTransfer.mozGetDataAt(sivFlavour,2); 

  // No Parent means delete element...
  if (this.sivParentId)
  {
    dom.addElement(this.sivParentId,elm,this.sivSiblingId);

    this.parentNode.insertBefore(
      event.dataTransfer.mozGetDataAt(sivFlavour,1)
      ,this).setAttribute("sivParentId",sivParentId);

    this.parentNode.insertBefore(
      event.dataTransfer.mozGetDataAt(sivFlavour,0)
      ,this);            
  }
  else
  {       
    // delete node...     
    event.dataTransfer.mozGetDataAt(sivFlavour,0).parentNode
      .removeChild(event.dataTransfer.mozGetDataAt(sivFlavour,0));

    event.dataTransfer.mozGetDataAt(sivFlavour,1).parentNode
      .removeChild(event.dataTransfer.mozGetDataAt(sivFlavour,1));
  }
}

this.createElement = function(sivFlavour,event) {
  var elm = SieveLexer.createByName(
              event.dataTransfer.mozGetDataAt(sivFlavour,1));

  dom.addElement(this.sivParentId,elm,this.sivSiblingId);

  this.parentNode.insertBefore(
    createDropTarget(this.sivParentId),this);

  this.parentNode.insertBefore(
    elm.toElement(),this);          
}

this.init();

handlers.handler:


this.init();

for (var i=0; i<this.sivFlavour.length; i++)
  if (this.canDrop(this.sivFlavour[i],event))
    this.setAttribute("sivDragging", "true");



this.init();

for (var i=0; i<this.sivFlavour.length; i++)
  if (this.canDrop(this.sivFlavour[i],event))
    this.removeAttribute("sivDragging");



this.init();

for (var i=0; i<this.sivFlavour.length; i++)
{
  if (this.canDrop(this.sivFlavour[i],event))
  {
    this.setAttribute("sivDragging", "true");

    event.stopPropagation(); 
    event.preventDefault();

    return;          
  }
}



this.init();

for (var i=0; i<this.sivFlavour.length; i++)
{
  if (this.canDrop(this.sivFlavour[i],event))
  {
    this.removeAttribute("sivDragging");       

    switch (event.dataTransfer.mozGetDataAt(this.sivFlavour[i],3))
    {
      case "move" :
        this.moveElement(this.sivFlavour[i],event);
        break;

      case "create" :
        this.createElement(this.sivFlavour[i],event);
        break;
    }
    return;
  }
}   

